// ---- BMI Calculator (simple math, no LLM needed) ----
export function calculateBMI(weightKg: number, heightCm: number) {
    const heightM = heightCm / 100;
    const bmi = weightKg / (heightM * heightM);
    let category = '';
    let color = '';
    if (bmi < 18.5) { category = 'Underweight'; color = '#40c4ff'; }
    else if (bmi < 25) { category = 'Normal'; color = '#00e676'; }
    else if (bmi < 30) { category = 'Overweight'; color = '#ffab00'; }
    else { category = 'Obese'; color = '#ff5252'; }
    return { bmi: Math.round(bmi * 10) / 10, category, color };
}

// ---- Hydration Calculator (simple math) ----
export function calculateHydration(weightKg: number, season: string, activityLevel: string) {
    let base = weightKg * 35;
    if (season === 'summer') base += 500;
    else if (season === 'monsoon') base += 200;
    else if (season === 'winter') base -= 200;
    if (activityLevel === 'active') base += 300;
    else if (activityLevel === 'sedentary') base -= 100;
    return { totalMl: Math.round(base), glasses: Math.round(base / 250), liters: (base / 1000).toFixed(1) };
}
